package token;public class Token {    private String tokenValue;    private TokenType tokenType;    private int tokenLine;    private int tokenPos;    public Token(String tokenvalue, TokenType tokenType, int tokenLine, int tokenPos) {        this.tokenValue=tokenvalue;        this.tokenLine=tokenLine;        this.tokenPos=tokenPos;        this.tokenType=tokenType;    }    public String getTokenName() {        return tokenValue;    }    public void setTokenName(String tokenName) {        this.tokenValue = tokenName;    }    public TokenType getTokenType() {        return tokenType;    }    public void setTokenType(TokenType tokenType) {        this.tokenType = tokenType;    }    public int getTokenLine() {        return tokenLine;    }    public void setTokenLine(int tokenLine) {        this.tokenLine = tokenLine;    }    public int getTokenPos() {        return tokenPos;    }    public void setTokenPos(int tokenPos) {        this.tokenPos = tokenPos;    }}